export class CustomerLoginComponent {
  username = '';
  password = '';

  loginForm: FormGroup;

  constructor(
    private _authService: AuthService,
    private router: Router,
    private _snackbar: MatSnackBar,
    private _fb: FormBuilder
  ) {
    this.loginForm = this._fb.group({
      username: ['', Validators.required],
      password: ['', Validators.required],
    });
  }

  onSubmit(): void {
    const form = this.loginForm;

    const data = {
      email: form.controls['username'].value,
      password: form.controls['password'].value,
    };
    this._authService.customerlogin(data).subscribe((res) => {
      if (res.success == true) {
        localStorage.setItem('userDetails', JSON.stringify(res.data));
        localStorage.setItem('authToken', res.jwt);
        this.router.navigateByUrl('my-ai-apps');

        this._authService.isUserLoggedIn$.next(true);
      } else {
        this._snackbar.open(res.message, undefined, { duration: 3000 });
      }
    });
  }
}
